import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        int count = 0;
        int current = 1;
        boolean[] visited = new boolean[n + 1];
        while (current != 2) {
            if (visited[current]) {
                System.out.println(-1);
                return;
            }
            visited[current] = true;
            current = a[current - 1];
            count++;
            if (count > n) {
                System.out.println(-1);
                return;
            }
        }
        System.out.println(count);
    }
}
