#include <stdio.h>

#define MOD 1000000007

long long factorial(int n) {
    long long res = 1;
    for (int i = 2; i <= n; i++)
        res = (res * i) % MOD;
    return res;
}

long long power(long long base, int exp) {
    long long res = 1;
    base %= MOD;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % MOD;
        base = (base * base) % MOD;
        exp /= 2;
    }
    return res;
}

long long inverse(long long n) {
    return power(n, MOD - 2);
}

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    if (abs(n - m) > 1) {
        printf("0\n");
        return 0;
    }

    long long ans = 0;
    if (n == m) {
        ans = (2 * factorial(n) * factorial(m)) % MOD;
    } else if (n == m + 1) {
        ans = factorial(n) * factorial(m) % MOD;
    } else {
        ans = factorial(n) * factorial(m) % MOD;
    }

    printf("%lld\n", ans);
    return 0;
}
