#include <iostream>
#include <algorithm>

using namespace std;

int main() {
  long long n, k;
  cin >> n >> k;

  long long min_val = -1;

  for (int i = 0; i <= n; ++i) {
    long long cur_val = 1;
    long long cur_n = n;

    for (int j = 0; j < i; ++j) {
      cur_val *= 2;
      cur_n--;
    }
    cur_val += cur_n * k;

    if (min_val == -1 || cur_val < min_val) {
      min_val = cur_val;
    }
  }
  cout << min_val << endl;
  return 0;
}
