import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long m = sc.nextLong();
        long mod = (long) Math.pow(10, 9) + 7;

        if (Math.abs(n - m) > 1) {
            System.out.println(0);
            return;
        }

        long ans = 0;
        if (n == m) {
            ans = (factorial(n) * factorial(m)) % mod * 2 % mod;

        } else if (n > m) {
            ans = (factorial(n) * factorial(m)) % mod;
        } else {
            ans = (factorial(n) * factorial(m)) % mod;
        }

        System.out.println(ans);
    }

    static long factorial(long n) {
        long res = 1;
        long mod = (long) Math.pow(10, 9) + 7;
        for (long i = 1; i <= n; i++) {
            res = (res * i) % mod;
        }
        return res;
    }
}
