import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] x = new long[n];
        long[] y = new long[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextLong();
            y[i] = sc.nextLong();
        }
        long ans = 0;
        Arrays.sort(x);
        Arrays.sort(y);
        ans += x[n - 1] - x[0];
        ans += y[n - 1] - y[0];
        System.out.println(ans);
    }
}
