#include <stdio.h>

long long factorial(int n, long long mod) {
    long long res = 1;
    for (int i = 2; i <= n; i++)
        res = (res * i) % mod;
    return res;
}

long long power(long long base, int exp, long long mod) {
    long long res = 1;
    base %= mod;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % mod;
        base = (base * base) % mod;
        exp /= 2;
    }
    return res;
}

int main() {
    int n, m;
    scanf("%d %d", &n, &m);
    long long mod = 1e9 + 7;
    if (abs(n - m) > 1) {
        printf("0\n");
        return 0;
    }
    long long ans = 0;
    if (n == m) {
        ans = (2 * factorial(n, mod) * factorial(m, mod)) % mod;
    } else if (n == m + 1) {
        ans = factorial(n, mod) * factorial(m, mod);
        ans %= mod;
    } else {
        ans = factorial(n, mod) * factorial(m, mod);
        ans %= mod;
    }
    printf("%lld\n", ans);
    return 0;
}
