#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, m;
  cin >> n >> m;

  long long mod = 1e9 + 7;

  if (abs(n - m) > 1) {
    cout << 0 << endl;
    return 0;
  }

  long long ans = 0;
  if (n == m) {
    vector<long long> fact(n + m + 1);
    fact[0] = 1;
    for (long long i = 1; i <= n + m; ++i) {
      fact[i] = (fact[i - 1] * i) % mod;
    }

    vector<long long> inv(n + m + 1);
    inv[n + m] = 1;
    long long cur = fact[n + m];
    for (long long i = n + m; i >= 1; --i) {
      long long power = mod - 2;
      long long res = 1;
      long long base = cur;

      while (power > 0) {
        if (power % 2 == 1) res = (res * base) % mod;
        base = (base * base) % mod;
        power /= 2;
      }
      inv[i-1] = res;
      cur = (cur * inv[i]) % mod;

    }

    ans = (2 * fact[n] * fact[m] % mod * inv[n] % mod * inv[m] % mod) % mod;
  } else if (n == m + 1) {
    vector<long long> fact(n + m + 1);
    fact[0] = 1;
    for (long long i = 1; i <= n + m; ++i) {
      fact[i] = (fact[i - 1] * i) % mod;
    }

    vector<long long> inv(n + m + 1);
    inv[n + m] = 1;
    long long cur = fact[n + m];
    for (long long i = n + m; i >= 1; --i) {
      long long power = mod - 2;
      long long res = 1;
      long long base = cur;

      while (power > 0) {
        if (power % 2 == 1) res = (res * base) % mod;
        base = (base * base) % mod;
        power /= 2;
      }
      inv[i-1] = res;
      cur = (cur * inv[i]) % mod;

    }
    ans = (fact[n] * fact[m] % mod * inv[n] % mod * inv[m] % mod) % mod;

  } else {
    vector<long long> fact(n + m + 1);
    fact[0] = 1;
    for (long long i = 1; i <= n + m; ++i) {
      fact[i] = (fact[i - 1] * i) % mod;
    }

    vector<long long> inv(n + m + 1);
    inv[n + m] = 1;
    long long cur = fact[n + m];
    for (long long i = n + m; i >= 1; --i) {
      long long power = mod - 2;
      long long res = 1;
      long long base = cur;

      while (power > 0) {
        if (power % 2 == 1) res = (res * base) % mod;
        base = (base * base) % mod;
        power /= 2;
      }
      inv[i-1] = res;
      cur = (cur * inv[i]) % mod;

    }
    ans = (fact[n] * fact[m] % mod * inv[n] % mod * inv[m] % mod) % mod;
  }

  cout << ans << endl;
  return 0;
}
