import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long m = sc.nextLong();
        long mod = 1000000007;

        if (Math.abs(n - m) > 1) {
            System.out.println(0);
            return;
        }

        BigInteger ans = BigInteger.ONE;
        BigInteger nFact = factorial(n, mod);
        BigInteger mFact = factorial(m, mod);

        if (n == m) {
            ans = ans.multiply(BigInteger.valueOf(2)).multiply(nFact).multiply(mFact);
        } else if (n > m) {
            ans = ans.multiply(nFact).multiply(mFact);

        } else {
            ans = ans.multiply(nFact).multiply(mFact);
        }

        System.out.println(ans.mod(BigInteger.valueOf(mod)));
    }

    public static BigInteger factorial(long n, long mod) {
        BigInteger fact = BigInteger.ONE;
        for (long i = 1; i <= n; i++) {
            fact = fact.multiply(BigInteger.valueOf(i)).mod(BigInteger.valueOf(mod));
        }
        return fact;
    }
}
