#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n, z, w;
  cin >> n >> z >> w;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long max_score = 0;

  function<void(int, long long, long long, bool)> solve = 
    [&](int idx, long long cur_z, long long cur_w, bool x_turn) {
    if (idx == n) {
      max_score = max(max_score, abs(cur_z - cur_w));
      return;
    }

    if (x_turn) {
      for (int i = idx; i < n; ++i) {
        solve(i + 1, a[i], cur_w, false);
      }
    } else {
      for (int i = idx; i < n; ++i) {
        solve(i + 1, cur_z, a[i], true);
      }
    }
  };

  solve(0, z, w, true);
  cout << max_score << endl;
  return 0;
}
