import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        long min = Long.MAX_VALUE;
        for (int i = 0; i <= n; i++) {
            long cur = 1L << (n - i);
            cur += (long) i * k;
            min = Math.min(min, cur);
        }
        System.out.println(min);
    }
}
