import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] x = new long[n];
        long[] y = new long[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextLong();
            y[i] = sc.nextLong();
        }
        long ans = 0;
        long minX = x[0];
        long minY = y[0];
        for (int i = 1; i < n; i++) {
            minX = Math.min(minX, x[i]);
            minY = Math.min(minY, y[i]);
        }
        for (int i = 0; i < n; i++) {
            ans += Math.min(Math.abs(x[i] - minX), Math.abs(y[i] - minY));
        }
        System.out.println(ans);
        sc.close();
    }
}
