#include <iostream>
#include <vector>

using namespace std;

int main() {
    long long r, c, n;
    cin >> r >> c >> n;

    vector<tuple<long long, long long, long long, long long>> pairs(n);
    for (int i = 0; i < n; ++i) {
        cin >> get<0>(pairs[i]) >> get<1>(pairs[i]) >> get<2>(pairs[i]) >> get<3>(pairs[i]);
    }

    if (n == 0) {
        cout << "YES" << endl;
        return 0;
    }

    long long x_min = r, x_max = 0, y_min = c, y_max = 0;
    for (int i = 0; i < n; i++) {
        x_min = min(x_min, min(get<0>(pairs[i]), get<2>(pairs[i])));
        x_max = max(x_max, max(get<0>(pairs[i]), get<2>(pairs[i])));
        y_min = min(y_min, min(get<1>(pairs[i]), get<3>(pairs[i])));
        y_max = max(y_max, max(get<1>(pairs[i]), get<3>(pairs[i])));
    }

    if (x_max - x_min > r || y_max - y_min > c) {
        cout << "NO" << endl;
        return 0;
    }

    vector<pair<long long, long long>> points;
    for (int i = 0; i < n; ++i) {
        points.push_back({get<0>(pairs[i]), get<1>(pairs[i])});
        points.push_back({get<2>(pairs[i]), get<3>(pairs[i])});
    }

    if (points.size() > 2 * r * c){
        cout << "NO" << endl;
        return 0;
    }


    cout << "YES" << endl;
    return 0;
}
