import java.util.*;
import java.io.*;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        long R = Long.parseLong(st.nextToken());
        long C = Long.parseLong(st.nextToken());
        int N = Integer.parseInt(st.nextToken());
        long[][] points = new long[N][4];
        for (int i = 0; i < N; i++) {
            st = new StringTokenizer(br.readLine());
            points[i][0] = Long.parseLong(st.nextToken());
            points[i][1] = Long.parseLong(st.nextToken());
            points[i][2] = Long.parseLong(st.nextToken());
            points[i][3] = Long.parseLong(st.nextToken());
        }
        if (N == 0) {
            System.out.println("YES");
            return;
        }
        long minX = Math.min(points[0][0], points[0][2]);
        long maxX = Math.max(points[0][0], points[0][2]);
        long minY = Math.min(points[0][1], points[0][3]);
        long maxY = Math.max(points[0][1], points[0][3]);
        for (int i = 1; i < N; i++) {
            long curMinX = Math.min(points[i][0], points[i][2]);
            long curMaxX = Math.max(points[i][0], points[i][2]);
            long curMinY = Math.min(points[i][1], points[i][3]);
            long curMaxY = Math.max(points[i][1], points[i][3]);
            if (maxX < curMinX || minX > curMaxX || maxY < curMinY || minY > curMaxY) {
                System.out.println("NO");
                return;
            }
            minX = Math.min(minX, curMinX);
            maxX = Math.max(maxX, curMaxX);
            minY = Math.min(minY, curMinY);
            maxY = Math.max(maxY, curMaxY);
        }
        System.out.println("YES");
    }
}
