#include <stdio.h>
#include <stdlib.h>

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    int L[N], R[N];
    for (int i = 0; i < N; i++) {
        scanf("%d %d", &L[i], &R[i]);
    }
    int chairs[M + 1];
    for (int i = 0; i <= M; i++) {
        chairs[i] = 0;
    }
    int count = 0;
    for (int i = 0; i < N; i++) {
        int placed = 0;
        for (int j = L[i]; j < R[i]; j++) {
            if (chairs[j] == 0) {
                chairs[j] = 1;
                placed = 1;
                break;
            }
        }
        if (!placed) {
            count++;
        }
    }
    printf("%d\n", count);
    return 0;
}

