import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int[] l = new int[n];
        int[] r = new int[n];
        for (int i = 0; i < n; i++) {
            l[i] = sc.nextInt();
            r[i] = sc.nextInt();
        }
        sc.close();

        int[] chairs = new int[m + 1];
        Arrays.fill(chairs, 0);
        int ans = 0;
        for (int i = 0; i < n; i++) {
            boolean seated = false;
            for (int j = 1; j <= m; j++) {
                if (j <= l[i] || j >= r[i]) {
                    if (chairs[j] == 0) {
                        chairs[j] = 1;
                        seated = true;
                        break;
                    }
                }
            }
            if (!seated) {
                ans++;
            }
        }
        System.out.println(ans);
    }
}
