#include <stdio.h>
#include <string.h>
#include <stdlib.h>

int solve() {
    char s[105];
    scanf("%s", s);
    int n = strlen(s);
    if (n == 0) return 0;
    int ans = 1000000;
    for (char c = 'a'; c <= 'z'; c++) {
        int cur = 0;
        char t[105];
        strcpy(t, s);
        while (1) {
            int flag = 0;
            for (int i = 0; i < strlen(t); i++) {
                if (t[i] != c) {
                    flag = 1;
                    break;
                }
            }
            if (!flag) break;
            char next[105] = "";
            int k=0;
            for (int i = 0; i < strlen(t) -1; i++) {
                if(t[i] == c || t[i+1] == c){
                    next[k++] = (t[i] == c)? t[i] : t[i+1];
                } else {
                    next[k++] = t[i];
                }
            }
            next[k] = '\0';
            strcpy(t,next);
            cur++;

        }
        ans = (ans < cur) ? ans : cur;
    }
    return ans;
}

int main() {
    printf("%d\n", solve());
    return 0;
}
