import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        int n = s.length();
        if (n == 0) {
            System.out.println(0);
            return;
        }
        int ans = Integer.MAX_VALUE;
        for (char c = 'a'; c <= 'z'; c++) {
            int cur = 0;
            Set<Character> set = new HashSet<>();
            for (int i = 0; i < n; i++) {
                set.add(s.charAt(i));
            }
            if (set.size() == 1 && s.charAt(0) == c) {
                ans = 0;
                break;
            }

            String t = s;
            while (true) {
                boolean allSame = true;
                for (int i = 1; i < t.length(); i++) {
                    if (t.charAt(i) != t.charAt(0)) {
                        allSame = false;
                        break;
                    }
                }
                if (allSame) break;

                String nextT = "";
                boolean changed = false;
                for (int i = 0; i < t.length() -1; i++) {
                    if (t.charAt(i) == c || t.charAt(i+1) == c) {
                        nextT += c;
                        changed = true;
                    } else {
                        nextT += t.charAt(i);
                    }
                }

                if (!changed) break;
                t = nextT;
                cur++;
            }
            boolean allSame = true;
            for (int i = 1; i < t.length(); i++) {
                if (t.charAt(i) != t.charAt(0)) {
                    allSame = false;
                    break;
                }
            }
            if (allSame) ans = Math.min(ans, cur + t.length() -1);

        }
        System.out.println(ans);
    }
}
