#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<int> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  if (n == 2) {
    if (a[0] == 1 && a[1] == 1) {
      cout << "Yes" << endl;
    } else {
      cout << "No" << endl;
    }
    return 0;
  }

  vector<int> counts(n,0);
  for(int i=0; i<n; ++i) counts[i] = a[i];

  sort(counts.begin(), counts.end());

  if (counts[0] == counts.back()) {
    if (counts[0] == 1 || counts[0] == n-1) {
        cout << "Yes" << endl;
    } else {
        cout << "No" << endl;
    }
  } else if (counts[0] == 1 && counts.back() == n-1){
      cout << "Yes" << endl;
  } else if(counts[0] == counts[n-2] && counts[n-1] == counts[0] + 1){
      cout << "Yes" << endl;
  }
   else {
    cout << "No" << endl;
  }

  return 0;
}
