#include <iostream>
#include <vector>

using namespace std;

int main() {
    long long H, W, h, w;
    cin >> H >> W >> h >> w;

    if (H == 1 || W == 1 || h == 1 && w ==1) {
        cout << "No" << endl;
        return 0;
    }

    if (h == H && w == W) {
        cout << "No" << endl;
        return 0;
    }


    if (h == 1 && w > 1){
        if (H * W == 2) {
            cout << "No" << endl;
            return 0;
        }
    }
    if (w == 1 && h > 1){
        if (H * W == 2) {
            cout << "No" << endl;
            return 0;
        }
    }

    vector<vector<long long>> matrix(H, vector<long long>(W, 1));
    long long total_sum = H * W;
    long long sub_sum = h * w;

    if (h * w == 1) {
        cout << "No" << endl;
        return 0;
    }


    if (h == 1 || w ==1) {
          cout << "No" << endl;
            return 0;
    }


    if (h == H && w == W) {
        cout << "No" << endl;
        return 0;
    }


    long long neg_val = - (total_sum + 1) / (H * W / sub_sum);

    if (neg_val >=0) {
         cout << "No" << endl;
         return 0;
    }

    for (long long i = 0; i < H ; ++i) {
        for (long long j = 0; j < W; ++j) {
            if (i < h && j < w) {
                matrix[i][j] = neg_val;
            }
        }
    }

    cout << "Yes" << endl;
    for (long long i = 0; i < H; ++i) {
        for (long long j = 0; j < W; ++j) {
            cout << matrix[i][j] << (j == W - 1 ? "" : " ");
        }
        cout << endl;
    }

    return 0;
}
