#include <stdio.h>
#include <string.h>
#include <stdlib.h>

int main() {
    char s_prime[51], t[51];
    scanf("%s %s", s_prime, t);
    int n = strlen(s_prime);
    int m = strlen(t);
    char best_s[51] = {};
    int found = 0;
    for (long long i = 0; i < pow(26, count(s_prime, s_prime + n, '?')); ++i) {
        long long temp = i;
        char s[51];
        strcpy(s, s_prime);
        for (int j = 0; j < n; ++j) {
            if (s[j] == '?') {
                s[j] = 'a' + temp % 26;
                temp /= 26;
            }
        }
        if (strstr(s, t) != NULL) {
            if (found == 0 || strcmp(s, best_s) < 0) {
                strcpy(best_s, s);
                found = 1;
            }
        }
    }
    if (found == 1) {
        printf("%s\n", best_s);
    } else {
        printf("UNRESTORABLE\n");
    }
    return 0;
}
