H, W, h, w = map(int, input().split())

if h == 1 and w == 1:
    print("No")
elif h == H and w == W:
    print("No")
else:
    matrix = [[0 for _ in range(W)] for _ in range(H)]
    for i in range(H):
        for j in range(W):
            if (i // h + j // w) % 2 == 0:
                matrix[i][j] = 1
            else:
                matrix[i][j] = -100

    print("Yes")
    for row in matrix:
        print(*row)

