#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    int a[N], b[N];
    int xor_a = 0, xor_b = 0;
    for (int i = 0; i < N; i++) {
        scanf("%d", &a[i]);
        xor_a ^= a[i];
    }
    for (int i = 0; i < N; i++) {
        scanf("%d", &b[i]);
        xor_b ^= b[i];
    }
    if (xor_a != xor_b) {
        printf("-1\n");
        return 0;
    }
    int count = 0;
    for (int i = 0; i < N; i++) {
        if (a[i] != b[i])
            count++;
    }
    if (count == 0)
        printf("0\n");
    else if (count == N)
        printf("1\n");
    else if (count % 2 == 0)
        printf("%d\n", count / 2);
    else
        printf("%d\n", count / 2 + 1);

    return 0;
}
