#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  vector<long long> b(n);
  for (int i = 0; i < n; ++i) {
    cin >> b[i];
  }

  long long xor_a = 0;
  for (long long x : a) {
    xor_a ^= x;
  }

  long long xor_b = 0;
  for (long long x : b) {
    xor_b ^= x;
  }

  if (xor_a != xor_b) {
    cout << -1 << endl;
    return 0;
  }

  int min_ops = 1e9;
  for (int i = 0; i < (1 << n); ++i) {
    vector<long long> cur_a = a;
    int ops = 0;
    long long cur_xor = xor_a;

    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        cur_a[j] = cur_xor;
        ops++;
        cur_xor = 0;
        for (long long x : cur_a) {
          cur_xor ^= x;
        }
      }
    }
    bool match = true;
    for (int j = 0; j < n; ++j) {
      if (cur_a[j] != b[j]) {
        match = false;
        break;
      }
    }
    if (match) {
      min_ops = min(min_ops, ops);
    }
  }
  if (min_ops == 1e9) {
      cout << -1 << endl;
  } else {
      cout << min_ops << endl;
  }

  return 0;
}
