public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        int[] b = new int[n];
        int xorA = 0;
        int xorB = 0;
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
            xorA ^= a[i];
        }
        for (int i = 0; i < n; i++) {
            b[i] = sc.nextInt();
            xorB ^= b[i];
        }
        if (xorA == xorB) {
            int count = 0;
            for (int i = 0; i < n; i++) {
                if (a[i] != b[i]) {
                    count++;
                }
            }
            if (count == 0) {
                System.out.println(0);
            } else if (count % 2 == 0) {
                System.out.println(count / 2);

            } else {
                boolean flag = false;
                for (int i = 0; i < n; i++) {
                    if (a[i] == xorA && b[i] != xorA) {
                        flag = true;
                        break;
                    }
                    if (a[i] != xorA && b[i] == xorA) {
                        flag = true;
                        break;
                    }

                }
                if(flag)
                    System.out.println((count + 1) / 2);
                else
                    System.out.println(-1);
            }
        } else {
            int count = 0;
            for (int i = 0; i < n; i++) {
                if (a[i] != b[i]) {
                    count++;
                }
            }
            if (count % 2 == 1) {
                System.out.println((count + 1) / 2 +1);
            } else {
                System.out.println(count / 2 + 2);
            }

        }
    }
}
