def solve():
    n = int(input())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))

    def count_ops(arr, target):
        ops = 0
        xor_sum = 0
        for x in arr:
            xor_sum ^= x

        diff_indices = []
        for i in range(n):
            if arr[i] != target[i]:
                diff_indices.append(i)

        if len(diff_indices) == 0:
            return 0

        if len(diff_indices) % 2 != 0:

            for i in diff_indices:
                temp_arr = arr[:]
                temp_arr[i] = xor_sum
                temp_xor = 0
                for x in temp_arr:
                    temp_xor ^= x

                if temp_xor == 0:
                    return len(diff_indices)
        else:
            return len(diff_indices)


    xor_a = 0
    for x in a:
        xor_a ^= x
    xor_b = 0
    for x in b:
        xor_b ^= x

    if xor_a != xor_b:
        print(-1)
        return

    print(count_ops(a,b))


