    int x[M], y[M];
    for (int i = 0; i < M; i++) {
        scanf("%d %d", &x[i], &y[i]);
        x[i]--; 
        y[i]--; 
    }
    int count = 0;
    for (int i = 0; i < N; i++) {
        for (int j = i + 1; j < N; j++) {
            int alive = (1 << N);
            int possible = 0;
            for (int k = 0; k < (1 << M); k++) {
                int temp_alive = alive;
                int valid = 1;
                for (int l = 0; l < M; l++) {
                    int a = x[l];
                    int b = y[l];
                    int eat;
                    if ((temp_alive & (1 << a)) && (temp_alive & (1 << b))) {
                        eat = ((k >> l) & 1) ? a : b;
                    } else if (temp_alive & (1 << a)) {
                        eat = a;
                    } else if (temp_alive & (1 << b)) {
                        eat = b;
                    } else {
                        eat = -1;
                    }
                    if (eat != -1) {
                        temp_alive &= ~(1 << eat);
                    }
                }
                if ((temp_alive & (1 << i)) && (temp_alive & (1 << j))) {
                    possible = 1;
                    break;
                }
            }
            if (possible) {
                count++;
            }
        }
    }
    printf("%d\n", count);
    return 0;
