#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, m;
  cin >> n >> m;

  vector<pair<int, int>> men(m);
  for (int i = 0; i < m; ++i) {
    cin >> men[i].first >> men[i].second;
    men[i].first--; 
    men[i].second--; 
  }

  long long count = 0;
  for (int i = 0; i < n; ++i) {
    for (int j = i + 1; j < n; ++j) {
      bool possible = false;
      for (int k = 0; k < (1 << m); ++k) {
        vector<bool> alive(n, true);
        bool i_alive = true;
        bool j_alive = true;

        for (int bit = 0; bit < m; ++bit) {
          int x = men[bit].first;
          int y = men[bit].second;

          if (alive[x] && alive[y]) {
            if ((k >> bit) & 1) {
              alive[x] = false;
            } else {
              alive[y] = false;
            }
          } else if (alive[x]) {
            alive[x] = false;
          } else if (alive[y]) {
            alive[y] = false;
          }
        }
        i_alive = alive[i];
        j_alive = alive[j];
        if (i_alive && j_alive) {
          possible = true;
          break;
        }
      }
      if (possible) {
        count++;
      }
    }
  }
  cout << count << endl;
  return 0;
}
