import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int[][] xy = new int[m][2];
        for (int i = 0; i < m; i++) {
            xy[i][0] = sc.nextInt() - 1;
            xy[i][1] = sc.nextInt() - 1;
        }

        long count = 0;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                boolean possible = false;
                for (int k = 0; k < (1 << m); k++) {
                    boolean[] alive = new boolean[n];
                    Arrays.fill(alive, true);
                    boolean i_alive = true;
                    boolean j_alive = true;
                    for (int l = 0; l < m; l++) {
                        int x = xy[l][0];
                        int y = xy[l][1];
                        if (alive[x] && alive[y]) {
                            if ((k >> l) % 2 == 0) {
                                alive[x] = false;
                            } else {
                                alive[y] = false;
                            }
                        } else if (alive[x]) {
                            alive[x] = false;
                        } else if (alive[y]) {
                            alive[y] = false;
                        }
                    }
                    i_alive = alive[i];
                    j_alive = alive[j];
                    if (i_alive && j_alive) {
                        possible = true;
                        break;
                    }
                }
                if (possible) {
                    count++;
                }
            }
        }
        System.out.println(count);
    }
}
