#include <iostream>
#include <string>
#include <vector>

using namespace std;

int main() {
  string s_prime, t;
  cin >> s_prime >> t;

  string best_s = "";
  bool found = false;

  vector<int> q_indices;
  for (int i = 0; i < s_prime.length(); ++i) {
    if (s_prime[i] == '?') {
      q_indices.push_back(i);
    }
  }

  int num_q = q_indices.size();
  if (num_q > 0) {
    vector<char> choices(num_q);

    function<void(int)> solve = [&](int k) {
      if (k == num_q) {
        string current_s = s_prime;
        for (int i = 0; i < num_q; ++i) {
          current_s[q_indices[i]] = choices[i];
        }

        if (current_s.find(t) != string::npos) {
          if (!found || current_s < best_s) {
            best_s = current_s;
            found = true;
          }
        }
        return;
      }

      for (char c = 'a'; c <= 'z'; ++c) {
        choices[k] = c;
        solve(k + 1);
      }
    };
    solve(0);
  } else {
    if (s_prime.find(t) != string::npos) {
      best_s = s_prime;
      found = true;
    }
  }

  if (found) {
    cout << best_s << endl;
  } else {
    cout << "UNRESTORABLE" << endl;
  }

  return 0;
}
