import java.util.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<int[]> edges = new ArrayList<>();
        for (int i = 0; i < m; i++) {
            edges.add(new int[]{sc.nextInt(), sc.nextInt()});
        }

        long count = 0;
        for (int i = 0; i < (1 << m); i++) {
            List<int[]> currentEdges = new ArrayList<>();
            for (int j = 0; j < m; j++) {
                if ((i >> j) % 2 == 0) {
                    currentEdges.add(edges.get(j));
                }
            }
            if (canWin(n, currentEdges)) {
                count++;
            }
        }
        System.out.println(count % MOD);
    }

    static boolean canWin(int n, List<int[]> edges) {
        boolean[][] adj = new boolean[n + 1][n + 1];
        for (int[] edge : edges) {
            adj[edge[0]][edge[1]] = true;
        }

        return canWin(adj, 1, 2, true);
    }

    static boolean canWin(boolean[][] adj, int p1, int p2, boolean aliceTurn) {
        boolean canMove = false;
        for (int i = 1; i < adj.length; i++) {
            if (adj[p1][i] || adj[p2][i]) {
                canMove = true;
                break;
            }
        }
        if (!canMove) {
            return !aliceTurn;
        }

        for (int i = 1; i < adj.length; i++) {
            if (adj[p1][i]) {
                if (!canWin(adj, i, p2, !aliceTurn)) {
                    return true;
                }
            }
            if (adj[p2][i]) {
                if (!canWin(adj, p1, i, !aliceTurn)) {
                    return true;
                }
            }
        }

        return false;
    }
}
