#include <stdio.h>
#include <stdlib.h>

int cmpfunc (const void * a, const void * b) {
   return ( *(int*)a - *(int*)b );
}

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    qsort(a, n, sizeof(int), cmpfunc);
    int sum = 0;
    for (int i = 0; i < n / 2; i++) {
        sum += a[n - 1 - i] - a[i];
    }
    printf("%d\n", sum * 2);
    return 0;
}
