#include <iostream>
#include <algorithm>
#include <vector>

int main() {
    int n;
    std::cin >> n;
    std::vector<long long> a(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> a[i];
    }
    std::sort(a.begin(), a.end());
    long long sum = 0;
    for (int i = 0; i < n / 2; ++i) {
        sum += a[n - 1 - i] - a[i];
    }
    std::cout << sum << std::endl;
    return 0;
}
