import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s_prime = sc.next();
        String t = sc.next();
        int n = s_prime.length();
        int m = t.length();
        String ans = "";
        char[] s = s_prime.toCharArray();
        boolean found = false;

        for (int i = 0; i < (int)Math.pow(26, countQuestionMarks(s)); ++i) {
            char[] cur = Arrays.copyOf(s, n);
            int temp = i;
            for (int j = 0; j < n; ++j) {
                if (cur[j] == '?') {
                    cur[j] = (char)('a' + temp % 26);
                    temp /= 26;
                }
            }
            String cur_str = new String(cur);
            if (cur_str.contains(t)) {
                if (ans.equals("") || cur_str.compareTo(ans) < 0) {
                    ans = cur_str;
                }
                found = true;
            }
        }

        if (found) {
            System.out.println(ans);
        } else {
            System.out.println("UNRESTORABLE");
        }
    }

    public static int countQuestionMarks(char[] s) {
        int count = 0;
        for (char c : s) {
            if (c == '?') {
                count++;
            }
        }
        return count;
    }
}
