#include <iostream>
#include <set>
#include <vector>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<int> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    set<string> colors;
    for (int rating : a) {
        if (rating >= 1 && rating <= 399) colors.insert("gray");
        else if (rating >= 400 && rating <= 799) colors.insert("brown");
        else if (rating >= 800 && rating <= 1199) colors.insert("green");
        else if (rating >= 1200 && rating <= 1599) colors.insert("cyan");
        else if (rating >= 1600 && rating <= 1999) colors.insert("blue");
        else if (rating >= 2000 && rating <= 2399) colors.insert("yellow");
        else if (rating >= 2400 && rating <= 2799) colors.insert("orange");
        else if (rating >= 2800 && rating <= 3199) colors.insert("red");
    }

    int min_colors = colors.size();
    int max_colors = 0;
    set<int> ratings;
    for (int rating : a) {
        ratings.insert(rating);
    }
    max_colors = min((int)ratings.size(),8);
    if(ratings.size() > 8) max_colors = 8;


    set<string> unique_colors;
    for (int x : a) {
        if (x >= 3200) continue;
        if (x >= 1 && x <= 399) unique_colors.insert("gray");
        else if (x >= 400 && x <= 799) unique_colors.insert("brown");
        else if (x >= 800 && x <= 1199) unique_colors.insert("green");
        else if (x >= 1200 && x <= 1599) unique_colors.insert("cyan");
        else if (x >= 1600 && x <= 1999) unique_colors.insert("blue");
        else if (x >= 2000 && x <= 2399) unique_colors.insert("yellow");
        else if (x >= 2400 && x <= 2799) unique_colors.insert("orange");
        else if (x >= 2800 && x <= 3199) unique_colors.insert("red");
    }

    int count3200 = 0;
    for(int x : a) if(x >= 3200) count3200++;
    max_colors = min( (int)unique_colors.size() + count3200, 8);
    if((int)unique_colors.size() + count3200 > 8) max_colors = 8;
    if(unique_colors.empty() && count3200 > 0) max_colors = min(count3200,8);
    if(unique_colors.empty() && count3200 == 0) max_colors = 0;


    if(min_colors == 0 && ratings.size() > 0) min_colors = 1;
    if(min_colors == 0 && ratings.size() == 0) min_colors = 0;
    cout << min_colors << " " << max_colors << endl;

    return 0;
}
