import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        Set<Integer> ratings = new HashSet<>();
        for (int i = 0; i < n; i++) {
            ratings.add(sc.nextInt());
        }
        int minColors = 0;
        int maxColors = 0;
        Map<Integer, String> colorMap = new HashMap<>();
        colorMap.put(1, "gray");
        colorMap.put(400, "brown");
        colorMap.put(800, "green");
        colorMap.put(1200, "cyan");
        colorMap.put(1600, "blue");
        colorMap.put(2000, "yellow");
        colorMap.put(2400, "orange");
        colorMap.put(2800, "red");

        Set<String> colors = new HashSet<>();
        for (int rating : ratings) {
            if (rating >= 3200) continue;
            String color = "";
            for (Map.Entry<Integer, String> entry : colorMap.entrySet()) {
                if (rating >= entry.getKey() && (entry.getKey() == 1 || rating <= colorMap.get(entry.getKey() + 399).equals("") ? false : Integer.parseInt(colorMap.get(entry.getKey() + 399).substring(0, colorMap.get(entry.getKey() + 399).indexOf(""))) < rating)) {
                  color = entry.getValue();
                }
            }
            colors.add(color);

        }
        minColors = colors.size();

        maxColors = colors.size();
        int count3200 = 0;
        for(int rating: ratings){
            if(rating >= 3200) count3200++;
        }
        maxColors += count3200;

        System.out.println(minColors + " " + maxColors);
        sc.close();
    }
}
