#include <stdio.h>
#include <string.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    char s[101];
    scanf("%s", s);
    int balance = 0, min_balance = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == '(')
            balance++;
        else
            balance--;
        min_balance = min(min_balance, balance);
    }
    int add_left = -min_balance;
    int add_right = balance - min_balance;
    char result[202];
    int k = 0;
    for (int i = 0; i < add_left; i++)
        result[k++] = '(';
    for (int i = 0; i < n; i++)
        result[k++] = s[i];
    for (int i = 0; i < add_right; i++)
        result[k++] = ')';
    result[k] = '\0';
    printf("%s\n", result);
    return 0;
}

