#include <iostream>
#include <string>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;
    string s;
    cin >> s;

    int balance = 0;
    int min_balance = 0;
    for (char c : s) {
        if (c == '(') {
            balance++;
        } else {
            balance--;
        }
        min_balance = min(min_balance, balance);
    }

    string result = "";
    for (int i = 0; i < -min_balance; ++i) {
        result += '(';
    }
    result += s;
    for (int i = 0; i < balance - min_balance; ++i) {
        result += ')';
    }

    cout << result << endl;
    return 0;
}
