s_prime, t = input(), input()
n, m = len(s_prime), len(t)
ans = "z" * n
for i in range(n - m + 1):
    s = list(s_prime)
    cur = ""
    for j in range(m):
        if s[i + j] == '?':
            s[i + j] = t[j]
        elif s[i + j] != t[j]:
            cur = ""
            break
        cur += s[i + j]
    if cur == t:
        for j in range(n):
            if s[j] == '?':
                s[j] = 'a'
        if "".join(s) < ans:
            ans = "".join(s)

if ans == "z" * n:
    print("UNRESTORABLE")
else:
    print(ans)

