#include <stdio.h>
#include <string.h>

int main() {
    char S[27];
    scanf("%s", S);
    int len = strlen(S);
    int flag = 0;
    for (int i = 0; i < len; i++) {
        for (int j = i + 1; j < len; j++) {
            if (S[i] == S[j]) {
                flag = 1;
                break;
            }
        }
        if (flag == 1) break;
    }
    if (flag == 1) printf("no\n");
    else printf("yes\n");
    return 0;
}
