#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    int scores[n];
    int total_score = 0;
    for (int i = 0; i < n; i++) {
        scanf("%d", &scores[i]);
        total_score += scores[i];
    }
    int max_displayed_score = 0;
    for (int i = 0; i < (1 << n); i++) {
        int current_score = 0;
        for (int j = 0; j < n; j++) {
            if ((i >> j) & 1) {
                current_score += scores[j];
            }
        }
        if (current_score % 10 != 0) {
            if (current_score > max_displayed_score) {
                max_displayed_score = current_score;
            }
        }
    }
    printf("%d\n", max_displayed_score);
    return 0;
}
