#include <iostream>
#include <vector>
#include <numeric>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<int> s(n);
  for (int i = 0; i < n; ++i) {
    cin >> s[i];
  }

  int max_displayed_grade = 0;
  for (int i = 0; i < (1 << n); ++i) {
    int current_grade = 0;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        current_grade += s[j];
      }
    }
    int displayed_grade = (current_grade % 10 == 0) ? 0 : current_grade;
    max_displayed_grade = max(max_displayed_grade, displayed_grade);
  }

  cout << max_displayed_grade << endl;
  return 0;
}
