n = int(input())
s = []
for _ in range(n):
    s.append(int(input()))

max_grade = 0
for i in range(1 << n):
    grade = 0
    for j in range(n):
        if (i >> j) & 1:
            grade += s[j]
    if grade % 10 != 0:
        max_grade = max(max_grade, grade)

print(max_grade)
