int main() {
    int N;
    scanf("%d", &N);
    int t[N], v[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &t[i]);
    }
    for (int i = 0; i < N; i++) {
        scanf("%d", &v[i]);
    }
    double ans = 0;
    for (int i = 0; i < (1 << N); i++) {
        double cur = 0;
        double cur_v = 0;
        double cur_t = 0;
        for (int j = 0; j < N; j++) {
            double accel_t = (double)v[j] - cur_v;
            double decel_t = cur_v;
            double maintain_t = t[j];
            if (accel_t > t[j]) {
                accel_t = t[j];
                maintain_t = 0;
            } else {
                maintain_t -= accel_t;
            }
            if (decel_t > t[j]) {
                decel_t = t[j];
                maintain_t = 0;
            } else {
                maintain_t -= decel_t;

            }
            if (accel_t > 0){
              cur += 0.5 * accel_t * accel_t;
              cur_v += accel_t;
            }
            if (maintain_t > 0)
            cur += cur_v * maintain_t;
            if (decel_t > 0){
              cur += cur_v * decel_t - 0.5 * decel_t * decel_t;
              cur_v -= decel_t;
            }

        }
        ans = (ans > cur) ? ans : cur;

    }
    printf("%.15lf\n", ans);
    return 0;
}
