import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long a = sc.nextLong();
        long b = sc.nextLong();
        long[] h = new long[(int) n];
        for (int i = 0; i < n; i++) {
            h[i] = sc.nextLong();
        }
        Arrays.sort(h);
        long ans = 0;
        for (int i = 0; i < n; i++) {
            if (h[i] <= 0) continue;
            long cur = h[i];
            long rem = cur - (n - 1 - i) * b;
            if (rem <= 0) {
                ans++;
            } else {
                long cnt = (rem + a - b - 1) / (a - b);
                ans += cnt;
            }
            for (int j = i + 1; j < n; j++) {
                h[j] -= b * (long) (ans- (i == 0 ? 0 : ans - (long)( (i > 0? (h[i-1] > 0 ? 1 : 0): 0))));
            }

        }
        System.out.println(ans);
    }
}
