import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] s = new int[n];
        int sum = 0;
        for (int i = 0; i < n; i++) {
            s[i] = sc.nextInt();
            sum += s[i];
        }
        Arrays.sort(s);
        int ans = 0;
        for (int i = 0; i < (1 << n); i++) {
            int cur = 0;
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    cur += s[j];
                }
            }
            if (cur % 10 != 0) {
                ans = Math.max(ans, cur);
            }
        }
        System.out.println(ans);
    }
}
