#include <stdio.h>

int main() {
    long long n, a, b;
    scanf("%lld %lld %lld", &n, &a, &b);
    long long h[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &h[i]);
    }
    long long ans = 0;
    while (1) {
        long long max_h = -1;
        int max_idx = -1;
        for (int i = 0; i < n; i++) {
            if (h[i] > max_h) {
                max_h = h[i];
                max_idx = i;
            }
        }
        if (max_h <= 0) break;
        ans++;
        h[max_idx] -= a;
        for (int i = 0; i < n; i++) {
            if (i != max_idx) {
                h[i] -= b;
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}
