#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n, a, b;
  cin >> n >> a >> b;

  vector<long long> h(n);
  for (int i = 0; i < n; ++i) {
    cin >> h[i];
  }

  long long ans = 0;
  while (true) {
    bool all_vanished = true;
    for (long long health : h) {
      if (health > 0) {
        all_vanished = false;
        break;
      }
    }
    if (all_vanished) break;

    int max_health_index = 0;
    for (int i = 1; i < n; ++i) {
      if (h[i] > h[max_health_index]) {
        max_health_index = i;
      }
    }

    ans++;
    for (int i = 0; i < n; ++i) {
      if (i == max_health_index) {
        h[i] -= a;
      } else {
        h[i] -= b;
      }
    }
  }
  cout << ans << endl;
  return 0;
}
