import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long a = sc.nextLong();
        long b = sc.nextLong();
        long[] h = new long[n];
        for (int i = 0; i < n; i++) {
            h[i] = sc.nextLong();
        }
        Arrays.sort(h);
        long ans = 0;
        for (int i = n - 1; i >= 0; i--) {
            if (h[i] <= 0) continue;
            long explosions = (h[i] + a - b - 1) / (a - b);
            ans += explosions;
            long dmg = explosions * b;
            for (int j = 0; j < i; j++) {
                h[j] -= dmg;
            }
        }
        System.out.println(ans);
    }
}
