n, a, b = map(int, input().split())
h = []
for _ in range(n):
    h.append(int(input()))

ans = 0
while True:
    max_h = 0
    max_index = -1
    for i in range(n):
        if h[i] > max_h:
            max_h = h[i]
            max_index = i
    if max_h <= 0:
        break
    ans += 1
    for i in range(n):
        if i == max_index:
            h[i] -= a
        else:
            h[i] -= b

print(ans)

