#include <stdio.h>

int main() {
    long long n, k;
    scanf("%lld %lld", &n, &k);
    long long a[n];
    for (long long i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    long long count = 0;
    for (long long i = 0; i < n; i++) {
        long long sum = 0;
        for (long long j = i; j < n; j++) {
            sum += a[j];
            if (sum * 1.0 / (j - i + 1) >= k) {
                count++;
            }
        }
    }
    printf("%lld\n", count);
    return 0;
}
